import React, { useEffect, useState } from 'react';
import { Header, Footer, LazyLoadImg, ScrollButton } from '../../components';
import {
    the_first_banner,
    corps_icon,
    categ_icon,
    bodies_icon,
    menu_icon4,
    case_red,
    tab_bluesmoke_l,
    tab_bluesmoke_r,
} from '../../assets/images';
import { BasicSlider } from '../../components';
import { Container, Col, Row, Tab, Nav } from 'react-bootstrap';
import {
    firstData,
    firstSettings,
    classification,
    classSettings,
    cropData,
    corpSettings,
    bodiesData,
    bodiesSettings,
} from './Data';

function MeetTheFirst() {

    const [datakey, setdatakey] = useState('the-first');

    useEffect(() => {
        document.title = "Meet The First | PlanerTota"
    });

    const tabItems = [
        {
            eventKey: 'the-first',
            img: menu_icon4,
            hd: 'The First',
            disc: {
                hd: 'The First',
                para: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima temporibus voluptate voluptatibus, id nisi, reprehenderit veniam laboriosam facere numquam possimus necessitatibus quisquam! Animi blanditiis fuga illo rem harum veniam tenetur! Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima temporibus voluptate voluptatibus, id nisi, reprehenderit veniam laboriosam facere numquam possimus necessitatibus quisquam! Animi blanditiis fuga illo rem harum veniam tenetur!'
            }
        },
        {
            eventKey: 'classification',
            img: categ_icon,
            hd: 'Classification',
            disc: {
                hd: 'Classification',
                para: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima temporibus voluptate voluptatibus, id nisi, reprehenderit veniam laboriosam facere numquam possimus necessitatibus quisquam! Animi blanditiis fuga illo rem harum veniam tenetur! Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima temporibus voluptate voluptatibus, id nisi, reprehenderit veniam laboriosam facere numquam possimus necessitatibus quisquam! Animi blanditiis fuga illo rem harum veniam tenetur!'
