import React, { useState, useRef } from 'react';
import Slider from 'react-slick';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

function HomeBanner(props) {
    const { content, id } = props;
    const [activeNav, setactiveNav] = useState(0);
    const slider = useRef();

    const settings = {
        dots: false,
        arrows: false,
        infinite: true,
        autoplay: true,
        lazyLoad: true,
        className: 'full_page_slider',
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        beforeChange: function (currentSlide, nextSlide) {
            setactiveNav(prev => prev = nextSlide);
        }
    }

    const updateSlide = (index) => {
        slider.current.slickGoTo(index);
        setactiveNav(prev => prev = index);
    }


    return (
        <section className='home_banner' id={id}>
            <Slider
                {...settings}
                ref={slider}
            >
                {
                    content.map((item, i) => {
                        return (
                            <div className="img_wrapper" key={i}>
                                <img
                                    src={item.img}
                                    alt={item.alt}
                                />
                            </div>
                        );
                    })
                }
            </Slider>
            <div className="dots_wrapper">
                <ul>
                    {content.map((v, i) => {
                        return <li key={i} className={activeNav === i ? 'active' : ''}><button onClick={() => updateSlide(i)}><FontAwesomeIcon icon={faCircle} /></button></li>
                    })}
                </ul>
            </div>
        </section>
    );
}

export default HomeBanner;
