import React, { useState, useEffect, useRef } from 'react';
import { Container } from 'react-bootstrap';
import Slider from 'react-slick';
import {
    arrows_down,
    case_classified,
    lock_bg,
    lock_dark,
    f8_fury,
    granted,
    welcome,
    case_mark,
    lock_screen,
    lock_slide,
    case_mark_dark,
    red_bar,
} from '../../assets/images';
import { LazyLoadImg } from '..';

function IndexSlider(props) {
    const { charData, settings, tab } = props;
    const charSlider = useRef(null);

    const [index, setindex] = useState();

    const handleClick = (i) => {
        if (i !== index) {
            setindex(prev => prev = i);
        }
    }

    useEffect(() => {
        setindex(charData[0].id);
    }, [charData]);

    return (
        <>
            <div className="slider_wrapper">
                <Slider
                    {...settings}
                    ref={charSlider}
                >
                    {charData.map((item, i) => {
                        return (
                            <div
                                className={index === item.id ? 'select item' : 'item'}
                                onClick={() => handleClick(item.id)}
                                key={i}>
                                {item.lockData ?
                                    <LazyLoadImg
                                        url={lock_slide}
                                        alt=''
                                    />
                                    :
                                    <LazyLoadImg
                                        url={item.img}
                                        alt=''
                                    />
                                }
                                <h5 className='title font_Helvetica'>{item.name}</h5>
                            </div>
                        );
                    })}
                </Slider>
                <div className="next_btn nav_btn">
                    <img src={arrows_down} alt="" onClick={() => charSlider.current.slickNext()} className="img_fluid" />
                </div>
                <div className="prev_btn nav_btn">
                    <img src={arrows_down} alt="" onClick={() => charSlider.current.slickPrev()} className="img_fluid" />
                </div>
            </div>
            {tab === 'meet-the-first-tab1' ?
                charData.filter(data => data.id === index).map((item, i) => {
                    let Item = item.profile;
                    return (
                        <TheFirstTabs
                            key={i}
                            Item={Item}
                        />
                    );
                })
                :
                null
            }
            {tab === 'metaverse-tab1' ?
                charData.filter(data => data.id === index).map((item, i) => {
                    let Item = item.profile;
                    return (
                        <MetaverseTab1
                            key={i}
                            Item={Item}
                            lock={item.lock}
                        />
                    );
                })
                :
                null
            }
            {tab === 'metaverse-tab3' ?
                charData.filter(data => data.id === index).map((item, i) => {
                    let Item = item.profile;
                    return (
                        <MetaverseTab3
                            key={i}
                            Item={Item}
                            lock={item.lockData}
                        />
                    );
                })
                :
                null
            }
        </>
    );
}

export default IndexSlider;

function TheFirstTabs(props) {
    const { Item } = props;
    return (
        <div className='character_bio' >
            <div className="inner_wrapper">
                <span className='profile_num'>
                    {Item.profileNum}
                    <font></font>
                </span>
                <div className="flex_wrapper">
                    <div className="left_col">
                        <div className="img_wrapper">
                            <div className="wrap">
                                <LazyLoadImg
                                    url={Item.sideImg1}
                                    alt=''
                                />
                            </div>
                            <div className="wrap">
                                <LazyLoadImg
                                    url={Item.sideImg2}
                                    alt=''
                                />
                            </div>
                        </div>
                        <div className="description">
                            <div className='tags_sec mb-2'>
                                <h5>Superpower</h5>
                                <ul>
                                    {
                                        Item.powers.map((item, i) => {
                                            return (
                                                <li key={i}><div className="badge"><span className='font_Helvetica'>{item}</span></div></li>
                                            );
                                        })
                                    }
                                </ul>
                            </div>
                            <div className='tags_sec'>
                                <h5>Classification</h5>
                                <ul>
                                    {
                                        Item.classification.map((item, i) => {
                                            return (
                                                <li key={i}><div className="badge"><span className='font_Helvetica'>{item}</span></div></li>
                                            );
                                        })
                                    }
                                </ul>
                            </div>
                            <p className='desc font_Helvetica'>
                                {Item.bio}
                            </p>
                        </div>
                    </div>
                    <div className="right_col">
                        <div className="img_wrapper">
                            <LazyLoadImg
                                url={case_classified}
                                alt=''
                                otherClass='case_classified'
                            />
                            <LazyLoadImg
                                url={Item.mainImage}
                                alt=''
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div >
    );
}

function MetaverseTab1(props) {
    const { Item, lock } = props;
    return (
        <div className='metaverse_tab1'>
            {lock === true ?
                <div className="lock_img">
                    <LazyLoadImg
                        url={lock_bg}
                        alt=''
                    />
                    <LazyLoadImg
                        url={lock_dark}
                        alt=''
                        otherClass='lock_icon'
                    />
                </div>
                :
                <div className="char_data">
                    <div className="top_sec">
                        <div className="inner_wrapper">
                            <div className="cover_img border_st">
                                <LazyLoadImg
                                    url={Item.charImg}
                                    alt=''
                                />
                                <LazyLoadImg
                                    url={f8_fury}
                                    alt=''
                                    otherClass='float_img'
                                />
                                <span className='top_left'></span>
                                <span className='top_right'></span>
                                <span className='bottom_right'></span>
                            </div>
                            <div className="wrap">
                                {
                                    Item.discription.map((item, i) => {
                                        return (
                                            <div className="left_content" key={i}>
                                                <h5><font>{item.font}</font><span>{item.span}</span></h5>
                                            </div>
                                        );
                                    })
                                }
                            </div>
                        </div>
                        <div className="position_img">
                            <LazyLoadImg
                                url={granted}
                                alt=''
                            />
                        </div>
                        <div className="welcome_img">
                            <LazyLoadImg
                                url={welcome}
                                alt=''
                            />
                        </div>
                    </div>
                    <div className="bottom_sec">
                        <Container fluid>
                            <div className="flex_wrap">
                                <div className="img_wrap">
                                    <LazyLoadImg
                                        url={Item.nameImg}
                                        alt=''
                                    />
                                </div>
                                <div className="wrap">
                                    <div className="tags_sec mb-2">
                                        <h5 className="font_Helvetica">Superpower</h5>
                                        <ul>
                                            {
                                                Item.superpower.map((item, i) => {
                                                    return (
                                                        <li key={i}>
                                                            <div className="badge">
                                                                <span className="font_Helvetica">{item}</span>
                                                            </div>
                                                        </li>
                                                    );
                                                })
                                            }
                                        </ul>
                                    </div>
                                    <div className="tags_sec">
                                        <h5 className="font_Helvetica">Classification</h5>
                                        <ul>
                                            {
                                                Item.classification.map((item, i) => {
                                                    return (
                                                        <li key={i}>
                                                            <div className="badge">
                                                                <span className="font_Helvetica">{item}</span>
                                                            </div>
                                                        </li>
                                                    );
                                                })
                                            }
                                        </ul>
                                    </div>
                                    <p className="para txt_white font_Helvetica">
                                        {Item.bio}
                                    </p>
                                </div>
                            </div>
                        </Container>
                        <LazyLoadImg
                            url={Item.smoke}
                            alt=''
                            otherClass='smoke'
                        />
                        <LazyLoadImg
                            url={case_mark}
                            alt=''
                            otherClass='case_mark'
                        />
                    </div>
                </div>
            }
        </div>
    );
}

function MetaverseTab3(props) {
    const { Item, lock } = props;
    return (
        <div className='metaverse_tab3'>
            {lock === true ?
                <div className="lock_img">
                    <LazyLoadImg
                        url={lock_screen}
                        alt=''
                    />
                </div>
                :
                <div className="location_info">
                    <div className="top_sec">
                        <div className="inner_wrapper">
                            <div className="flex_wrapper">
                                <div className="cover_img border_st">
                                    <LazyLoadImg
                                        url={Item.mainImg}
                                        alt=''
                                    />
                                    <span className='top_left'></span>
                                    <span className='top_right'></span>
                                    <span className='bottom_left'></span>
                                    <span className='bottom_right'></span>
                                </div>
                                <div className="side_img">
                                    <div className="first_img border_st">
                                        <LazyLoadImg
                                            url={Item.sideImg1}
                                            alt=''
                                        />
                                        <span className='top_left'></span>
                                        <span className='top_right'></span>
                                        <span className='bottom_left'></span>
                                        <span className='bottom_right'></span>
                                    </div>
                                    <div className="second_img border_st">
                                        <LazyLoadImg
                                            url={Item.sideImg2}
                                            alt=''
                                        />
                                        <span className='top_left'></span>
                                        <span className='top_right'></span>
                                        <span className='bottom_left'></span>
                                        <span className='bottom_right'></span>
                                    </div>
                                </div>
                            </div>
                            <div className="discription">
                                <LazyLoadImg
                                    url={case_mark_dark}
                                    alt=''
                                    otherClass='case_mark_dark'
                                />
                                <div className="wrap">
                                    {
                                        Item.discription.map((item, i) => {
                                            return (
                                                <div className="left_content" key={i}>
                                                    <h5><font>{item.font}</font><span>{item.span}</span></h5>
                                                </div>
                                            );
                                        })
                                    }
                                </div>
                            </div>
                        </div>
                        <div className="doc">
                            <p className='font_Typewriter'>{Item.doc}</p>
                        </div>
                        <div className="case_no">
                            <p className='font_Typewriter'>{Item.case}</p>
                        </div>
                        <LazyLoadImg
                            url={red_bar}
                            alt=''
                            otherClass='red_bar'
                        />
                    </div>
                    <div className="bottom_sec">
                        <Container>
                            <p className="para txt_white font_Helvetica">
                                {Item.disc}
                            </p>
                        </Container>
                        <LazyLoadImg
                            url={Item.smoke}
                            alt=''
                            otherClass='smoke'
                        />
                        <LazyLoadImg
                            url={case_mark}
                            alt=''
                            otherClass='case_mark'
                        />
                    </div>
                </div>
            }
        </div>
    );
}
