import React, { useEffect } from 'react'
import { CountDownTimer, Header, HomeBanner, Footer, Loader } from '../../components';
import { banners, slides, menuIcons } from './Data';
import { useNavigate } from 'react-router-dom';
import { HashLink } from 'react-router-hash-link';


function HomePage() {

    useEffect(() => {
        document.title = "Home | PlanerTota";
    });

    const navigate = useNavigate();

    return (
        <>
            <Header />
            <HomeBanner
                content={slides}
                id='req_sec1'
            />
            <section className='home_sec'>
                <div className='sec_nav'>
                    <div className="flex_wrapper">
                        {
                            menuIcons.map((item, i) => {
                                return (
                                    <HashLink
                                        className='icon_wrapper active'
                                        key={i}
                                        smooth
                                        to={item.goto}
                                    >
                                        <img src={item.img} alt="" className="img_fluid" />
                                    </HashLink>
                                    // <div
                                    //     className='icon_wrapper active'
                                    //     key={i}
                                    //     onClick={() => window.location.replace(`/#${item.goto}`)}>
                                    //     <img src={item.img} alt="" className="img_fluid" />
                                    //     {/* {i === 0 &&
                                    //         <div className="active_icon">
                                    //             <FontAwesomeIcon icon={faSortDown} />
                                    //         </div>
                                    //     } */}
                                    // </div>
                                );
                            })
                        }
                    </div>
                </div>
                {banners.map((item, i) => {
                    return (
                        <button
                            className='link_btn'
                            id={item.id}
                            onClick={() => navigate(item.url)}
                            key={i}>
                            <Loader
                                src={item.img}
                            />
                            {
                                item.timer ?
                                    <div className="counter">
                                        <font>{item.timer.ep}</font>
                                        <h4>
                                            {item.timer.hd}
                                        </h4>
                                        <h5>{item.timer.subHd}</h5>
                                        <CountDownTimer
                                            countdown='02/01/2022'
                                        />
                                    </div>
                                    :
                                    ''
                            }
                        </button>
                    );
                })}
            </section>
            <Footer
                fooClass='home_foo'
            />
        </>
    );
}

export default HomePage;
