import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import CustomDropdown from './dropdown';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSortDown } from '@fortawesome/free-solid-svg-icons';
import useMediaQuery from 'react-hook-media-query';


function MyNav(props) {

    const media = useMediaQuery('(max-width: 991px)');
    const navigate = useNavigate();

    return (
        <ul className={props.isActive ? 'nav_ul active' : 'nav_ul'}>
            <span className='close_btn' onClick={() => props.close()}>&times;</span>
            {
                props.navItems.map((item, i) => {
                    return (
                        <li key={i}>
                            {item.url ?
                                <NavLink
                                    to={item.url}
                                    className="nav_link"
                                >
                                    <div className="common_icon_st">
                                        <img src={item.img} alt="" className="img_fluid" />
                                    </div>
                                    {item.txt}</NavLink>
                                :
                                <button
                                    className='nav_link'
                                    onClick={() => media ? navigate('/') : console.log('false')}
                                >
                                    <div className="common_icon_st">
                                        <img src={item.img} alt="" className="img_fluid" />
                                    </div>
                                    {item.txt}
                                    {item.dropdown && <FontAwesomeIcon icon={faSortDown} />}
                                </button>
                            }
                            {
                                item.dropdown &&
                                <CustomDropdown dropItem={item.dropdown} />
                            }
                        </li>
                    );
                })
            }
        </ul >
    );
}

export default MyNav;
