import React, { useState, useEffect } from 'react'
import { Header, Footer, LazyLoadImg } from '../../components';
import {
    collab_hd,
    wow_hd,
    f8_hd,
    exp_char1,
    exp_char2,
    exp_char3,
    exp_char4,
    exp_char5,
    exp_char6,
} from '../../assets/images';

function ExploreSynergies() {

    useEffect(() => {
        document.title = "Explore Synergies | PlanerTota";
    });
    return (
        <>
            <Header />
            <main>
                <div className='main_heading'>
                    <h2 className='font_Helvetica text-center text-md-start'>pick your interest</h2>
                </div>

                <ExpSections
                    otherClass='first'
                    character1={exp_char1}
                    character2={exp_char2}
                    titleImg={collab_hd}
                    para='Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam
                    erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo
                    consequat. Duis autem vel eum iriure dolor in hendrerit in vulputate velit esse molestie consequat, vel illum dolore eu feugiat nulla
                    facilisis at vero eros et accumsanLorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt
                    ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis
                    nisl ut aliquip ex ea commodo consequat. Duis autem vel eum iriure dolor in hendrerit in vulputate velit esse molestie'
                    btntxt='drop a note'
                    successMsg='Thank you for reaching out. We will get in touch with you at the earliest'
                    bottomPara='Or Write to us at collab@planettota.com'
                />

                <ExpSections
                    otherClass='second'
                    character1={exp_char3}
                    character2={exp_char4}
                    titleImg={wow_hd}
                    para='Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam
                    erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo
                    consequat. Duis autem vel eum iriure dolor in hendrerit in vulputate velit esse molestie consequat, vel illum dolore eu feugiat nulla
                    facilisis at vero eros et accumsanLorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt
                    ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis
                    nisl ut aliquip ex ea commodo consequat. Duis autem vel eum iriure dolor in hendrerit in vulputate velit esse molestie'
                    btntxt='drop a note'
                    successMsg='Thank you for reaching out. We will get in touch with you at the earliest'
                    bottomPara='Or Write to us at wow@planettota.com'
                />

                <ExpSections
                    otherClass='third'
                    character1={exp_char5}
                    character2={exp_char6}
                    titleImg={f8_hd}
                    para='Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam
                    erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis nisl ut aliquip ex ea commodo
                    consequat. Duis autem vel eum iriure dolor in hendrerit in vulputate velit esse molestie consequat, vel illum dolore eu feugiat nulla
                    facilisis at vero eros et accumsanLorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt
                    ut laoreet dolore magna aliquam erat volutpat. Ut wisi enim ad minim veniam, quis nostrud exerci tation ullamcorper suscipit lobortis
                    nisl ut aliquip ex ea commodo consequat. Duis autem vel eum iriure dolor in hendrerit in vulputate velit esse molestie'
                    btntxt='drop a note'
                    successMsg='Thank you for reaching out. We will get in touch with you at the earliest'
                    bottomPara='Or Write to us at ip@planettota.com'
                />

            </main>
            <Footer />
        </>
    );
}

export default ExploreSynergies;

function ExpSections(props) {
    const [showMsg, setshowMsg] = useState(false);
    const [loadForm, setloadForm] = useState(false);

    const [formData, setformData] = useState({
        fullName: '',
        email: '',
        cName: '',
        phone: '',
        message: ''
    });

    const handleChange = (e) => {
        if (e.target.name === 'full-name') {
            setformData(prev => ({ ...prev, fullName: e.target.value }));
        }
        if (e.target.name === 'phone') {
            setformData(prev => ({ ...prev, phone: e.target.value }));
        }
        if (e.target.name === 'c-name') {
            setformData(prev => ({ ...prev, cName: e.target.value }));
        }
        if (e.target.name === 'email') {
            setformData(prev => ({ ...prev, email: e.target.value }));
        }
        if (e.target.name === 'message') {
            setformData(prev => ({ ...prev, message: e.target.value }));
        }
    }

    const handleSubmit = (ev) => {
        ev.preventDefault();
        console.log('>>', formData);
        setformData({ fullName: '', cName: '', phone: '', email: '', message: '' });
        setshowMsg(true);
        setloadForm(false);
    }

    const { otherClass, character1, character2, titleImg, para, btntxt, successMsg, bottomPara } = props;
    return (
        <section className={otherClass + ' exp_characters'}>
            <div className="img_wrapper character1">
                <div className="extra_layer">
                    <div className="bg_color skyBlue"></div>
                    <LazyLoadImg
                        url={character1}
                        alt=''
                    />
                </div>
            </div>
            <div className="content-box green_box">
                {loadForm ?
                    <>
                        <div className="img_box ps-0 ps-md-5 pt-0 pt-md-4 mb-4">
                            <LazyLoadImg
                                url={titleImg}
                                alt=''
                            />
                        </div>
                        <form
                            className='global_form'
                            action=""
                            autoComplete='off'
                            onSubmit={handleSubmit}
                        >
                            <div className="global_input_wrapper">
                                <input
                                    type="text"
                                    required
                                    name='full-name'
                                    value={formData.fullName}
                                    onChange={handleChange}
                                    className='default border-0'
                                    placeholder='Name*' />
                            </div>
                            <div className="global_input_wrapper">
                                <input
                                    type="email"
                                    required
                                    name='email'
                                    value={formData.email}
                                    onChange={handleChange}
                                    className='default border-0'
                                    placeholder='Email*' />
                            </div>
                            <div className="global_input_wrapper">
                                <input
                                    type="text"
                                    name='c-name'
                                    value={formData.cName}
                                    onChange={handleChange}
                                    className='default border-0'
                                    placeholder="Company's Name" />
                            </div>
                            <div className="global_input_wrapper">
                                <input
                                    type="text"
                                    name='phone'
                                    value={formData.phone}
                                    onChange={handleChange}
                                    className='default border-0'
                                    placeholder='Phone Number' />
                            </div>
                            <div className="global_input_wrapper mb-0">
                                <textarea
                                    name="message"
                                    value={formData.message}
                                    onChange={handleChange}
                                    className='default border-0'
                                    rows='7'
                                    placeholder='Message'></textarea>
                            </div>
                            <div className="global_input_wrapper text-center">
                                <button
                                    type='submit'
                                    className='btn text-uppercase'
                                ><strong>Submit</strong></button>
                            </div>

                        </form>
                    </>
                    :
                    <div className="inner_wrapper">
                        <div className="img_box ps-0 ps-md-5 pt-0 pt-md-4">
                            <LazyLoadImg
                                url={titleImg}
                                alt=''
                            />
                        </div>
                        <p>
                            {showMsg ? successMsg : para}
                        </p>
                        {showMsg ? null
                            :
                            <div className="text-center">
                                <button
                                    className="btn"
                                    onClick={() => setloadForm(true)}
                                >{btntxt}</button>

                                <p>{bottomPara}</p>
                            </div>
                        }
                    </div>
                }
            </div>
            <div className="img_wrapper character2">
                <div className="extra_layer">
                    <div className="bg_color"></div>
                    <LazyLoadImg
                        url={character2}
                        alt=''
                    />
                </div>
            </div>
        </section>
    );
